import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const data = { "_id": "679bc54eb73000d63b65527f", "nombre": "Trivia 30", "descripcion": "", "tipo": "Trivia", "geometry": { "type": "Point", "coordinates": [-73.25103056432454, -39.803365788006914] }, "paginas": [{ "enunciado": "Cuanto miden los Olivillos", "imagen": "../../images/Trivia30_pagina_1.jpg", "mezclarRespuestas": false, "respuestas": [{ "texto": "10 metros", "correcta": false }, { "texto": "20 metros", "correcta": true }, { "texto": "30 metros", "correcta": false }] }] };

const Trivia = () => {
    const [currentPage, setCurrentPage] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);
    const [answerVerified, setAnswerVerified] = useState(false);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [startTime, setStartTime] = useState(Date.now());
    const navigate = useNavigate();
    const [lives, setLives] = useState(3);
    const [correct, setCorrect] = useState(null);

    useEffect(() => {
        if (data.shufflePages) {
            data.paginas.sort(() => Math.random() - 0.5);
        }
    }, []);

    const handleSelection = (index) => {
        setSelectedOption(index);
    };

    const verifyAnswer = () => {
        if (selectedOption === null) return;
        console.log("Pagina actual: ", currentPage, "Respuesta seleccionada: ", selectedOption);

        // Verificar si la respuesta seleccionada existe
        if (data.paginas[currentPage-1]?.respuestas[selectedOption]) {
            const isCorrect = data.paginas[currentPage].respuestas[selectedOption].correcta;
            if (isCorrect) {
                setCorrectAnswers(correctAnswers + 1);
                setCorrect(True);

            } else setCorrect(False);
            setAnswerVerified(true);
        } else {
            console.error('Respuesta no válida');
        }
    };

    const nextPage = () => {
        setAnswerVerified(false);
        setSelectedOption(null);
        setCorrect(null);
        setCurrentPage(currentPage + 1);
    };

    return (
        <div className={`h-screen w-full ${currentPage > 0 && currentPage < data.paginas.length + 1 ? "bg-white" : "bg-lime-500"}`}>
            <div className={"p-4 max-w-lg mx-auto"}>
                <div className="flex items-center justify-between w-full space-x-4 mb-4">
                    {/* Cuadro izquierdo */}
                    <div
                        className="w-12 h-12 text-400 cursor-pointer"
                        onClick={() => navigate("/map")}
                    ></div>

                    {/* Barra central */}
                    <div className="flex-1 h-4 bg-gray-200 relative rounded-full">
                        <div
                            className="absolute top-0 left-0 h-full bg-lime-500 rounded-full"
                            style={{ width: `${(currentPage / 10) * 100}%` }}
                        ></div>
                    </div>

                    {/* Cuadro derecho */}
                    <div className="w-12 h-12 text-red-500 flex items-center justify-center font-bold">
                        {lives}
                    </div>
                </div>

                {/* Presentacion */}
                {currentPage === 0 && (
                    <div className="flex flex-col items-center justify-center h-screen text-center">
                        <h1 className="text-4xl font-bold text-white mb-6">{data.nombre}</h1>
                        <p className="text-gray-700 mb-6">{data.descripcion}</p>
                        <button
                            className="bg-blue-500 text-white px-8 py-4 text-xl font-bold rounded-xl"
                            onClick={() => setCurrentPage(1)}
                        >
                            JUGAR
                        </button>
                    </div>
                )}

                {/* Paginas de la trivia */}
                {currentPage > 0 && currentPage < data.paginas.length && (
                    <>
                        {data.paginas[currentPage - 1] && (
                            <>
                                <h2 className="text-xl font-semibold mb-2">
                                    {data.paginas[currentPage - 1].enunciado}
                                </h2>
                                {data.paginas[currentPage - 1].imagen && (
                                    <img
                                        src={data.paginas[currentPage - 1].imagen}
                                        alt="Imagen"
                                        className="mb-4 w-full h-auto rounded-xl"
                                    />
                                )}
                                <div className="grid grid-cols-2 gap-2 mb-4">
                                    {data.paginas[currentPage - 1].respuestas.map((answer, index) => (
                                        <button
                                            key={index}
                                            className={`py-4 rounded-xl border-2 ${selectedOption === index
                                                ? "text-blue-600 bg-blue-200 border-blue-400 "
                                                : "text-black bg-white border-gray-200 border"
                                                }`}
                                            onClick={() => handleSelection(index)}
                                            disabled={answerVerified}
                                        >
                                            {answer.texto}
                                        </button>
                                    ))}
                                </div>

                                {/* Boton para comprobar respuestas*/}
                                {!answerVerified && (
                                    <button
                                        className={`w-full py-3 font-bold text-white rounded-xl ${selectedOption === null
                                            ? "bg-gray-200"
                                            : "bg-lime-500"
                                            }`}
                                        onClick={verifyAnswer}
                                        disabled={selectedOption === null}
                                    >
                                        COMPROBAR
                                    </button>
                                )}

                                {/* Boton para avanzar*/}
                                {answerVerified && (
                                    <button
                                        className={`w-full py-3 font-bold text-white rounded-xl ${correct === True
                                            ? "bg-lime-500"
                                            : "bg-red-500"
                                            }`}
                                        onClick={nextPage}
                                    >
                                        {correct === true ? "SIGUIENTE" : "ENTENDIDO"}
                                    </button>
                                )}

                            </>
                        )}
                    </>
                )}

                {/* Página final (resultados) */}
                {currentPage === data.paginas.length + 1 && (
                    <>
                        <h2 className="text-2xl font-bold mb-4">Resultados</h2>
                        <p className="text-lg mb-2">Respuestas correctas: {correctAnswers}</p>
                        <p className="text-lg mb-4">Tiempo: {Math.floor((Date.now() - startTime) / 1000)} segundos</p>
                        <button
                            className="bg-blue-500 text-white px-4 py-2 rounded-xl"
                            onClick={navigate("/map")}
                        >
                            Volver al mapa
                        </button>
                    </>
                )}
            </div>
        </div>
    );
};

export default Trivia;
